addEventHandler('onClientRender', root, function()
    if (getElementData(localPlayer, 'battleRoyaleMatch')) then 
        if getElementData(localPlayer, 'myDuo') then
            local x, y, w, h = setScreenPosition(10, 644, 172, 42)
            dxDrawImage(x, y, w, h, bg, 0, 0, 0, tocolor(255, 255, 255, 255), true)
        else
            local x2, y2, w2, h2 = setScreenPosition(10, 708, 172, 42)
            dxDrawImage(x2, y2, w2, h2, bg, 0, 0, 0, tocolor(255, 255, 255, 255), true)
        end
        local duo = getElementData(localPlayer, 'myDuo')
        if duo then
            if getElementData(duo, 'groupOwner') then
                local both = getElementData(localPlayer, 'follow')
                local x2, y2, w2, h2 = setScreenPosition(605, 643, 153, 43)
                dxDrawImage(x2, y2, w2, h2, (both and seguir2 or seguir1), 0, 0, 0, tocolor(255, 255, 255, 255), true)
            end
        end
    end
end)

addEvent("audioAq", true)
addEventHandler("audioAq", resourceRoot, function(audioPath)
    playSound(audioPath)
end)

local roomCodeText = ""
local roomPlayerCount = 0

addEvent("showRoomCode", true)
addEventHandler("showRoomCode", resourceRoot, 
function(roomCode)
    roomCodeText = "Código da sala: " .. roomCode
end
)


addEvent("updateCustomRoomPlayerCount", true)
addEventHandler("updateCustomRoomPlayerCount", resourceRoot, 
function(playerCount)
    roomPlayerCount = playerCount
end
)

addEvent("hideRoomCode", true)
addEventHandler("hideRoomCode", resourceRoot, 
function()
    roomCodeText = ""
    playerCountText = ""
end
)

addEvent("openCabine", true)
addEventHandler("openCabine", root, function(vehicle)
    setVehicleComponentVisible(vehicle, "misc_b", false)
end)

addEventHandler("onClientRender", root, function()
    if roomCodeText ~= "" then
        local screenWidth, screenHeight = guiGetScreenSize()
        dxDrawText(roomCodeText, screenWidth / 2, screenHeight - 50, screenWidth / 2, screenHeight - 50, tocolor(255, 255, 255), 1, "default-bold", "center", "center")
        local playerCountText = "Jogadores na sala: " .. roomPlayerCount
        dxDrawText(playerCountText, x * (636/1366), y * (571/768), x * (20/1366), y * (20/768), tocolor(255, 255, 255), 1, "default-bold", "right", "top")
    end
end
)


addEventHandler("onClientRender", root, function()
    local playersnoaviao = 0
    if (getElementData(localPlayer, 'battleRoyaleMatch')) then
        local localDimension = getElementDimension(localPlayer)
        for _, player in ipairs(getElementsByType("player")) do
            if getElementData(player, "battleRoyaleMatch") and getElementDimension(player) == localDimension then
                playersnoaviao = playersnoaviao + 1
            end
        end
        dxDrawImage(597, 33, 172, 30, "assets/images/aviaoqtd.png")
        dxDrawText('Restam '..playersnoaviao..' no avião', 641, 41, 109, 16, tocolor(255, 255, 255), 1, "default-bold", "left", "top")
    end
end
)

function drawTimer()
    local alpha = interpolateBetween(0, 0, 0, 255, 0, 0, ((getTickCount() - timeTick) / 500), 'Linear')
    local time = interpolateBetween(remainTime, 0, 0, 0, 0, 0, ((getTickCount() - timeTick) / remainTime), 'Linear')

    if getElementData(localPlayer, 'myDuo') then
        local x, y, w, h = setScreenPosition(10, 644, 172, 42)
        dxDrawImage(x, y, w, h, bg_sairpartida, 0, 0, 0, tocolor(255, 255, 255, 255), true)
    else
        local x2, y2, w2, h2 = setScreenPosition(10, 708, 172, 42)
        dxDrawImage(x2, y2, w2, h2, bg_sairpartida, 0, 0, 0, tocolor(255, 255, 255, 255), true)
    end
    
    local min, sec = convertTime(time)
    local text = '(PAREANDO '..pairMembers..'/'..config.maxMembers..')'
    dxDrawText(text,  x * (632.5/1366), y * (589/768), x * (20/1366), y * (20/768), tocolor(255, 255, 255, alpha), 1, fonts[2])
end 

addEvent('onClientDrawBattleRoyaleTimer', true)
addEventHandler('onClientDrawBattleRoyaleTimer', root, 
function(remainTime_, pairMembers_)
    timeTick, remainTime, pairMembers = getTickCount(), remainTime_, pairMembers_
    
    if not (isEventHandlerAdded('onClientRender', root, drawTimer)) then 
        addEventHandler('onClientRender', root, drawTimer)
        triggerEvent("progressBar", resourceRoot, remainTime)
    end
end
)

function sairPartidaF()
    if getElementData(localPlayer, "runyInPareamento") then
        local receiver = getElementData(localPlayer, 'myDuo')
        if receiver then
            if getElementData(localPlayer, 'groupOwner') then
                triggerEvent("returnToLobbyCancelMatch", root, receiver)
                triggerEvent("returnToLobbyCancelMatch", root, localPlayer)
            else
                triggerEvent("returnToLobbyCancelMatch", root, localPlayer)
                setElementData(localPlayer, 'myDuo', false)
                setElementData(localPlayer, 'groupOwner', false)
            end
        end
        triggerEvent("returnToLobbyCancelMatch", root, localPlayer)
    end
end
bindKey("F", "down", sairPartidaF)


function following()
    if getElementData(localPlayer, "battleRoyaleMatch") then
        local receiver = getElementData(localPlayer, 'myDuo')
        if receiver then
            if not getElementData(localPlayer, 'groupOwner') then
                local follow = getElementData(localPlayer, 'follow')
                setElementData(localPlayer, 'follow', (not follow and receiver or false))
            end
        end
    end
end
