function PlaySounds(som, player, volume, distance, object)
    if fileExists( "assets/"..som..".wav" ) then
        if object then
            local x,y,z = getElementPosition(object)
            local sound = playSound3D("assets/"..som..".wav", x, y, z)
            setSoundMinDistance( sound, 4)
            setSoundMaxDistance( sound, distance)
            setSoundVolume( sound, volume )
            local dim = getElementDimension(object)
            local int = getElementInterior(object)
            setElementDimension(sound, dim)
            setElementInterior(sound, int)
            
            attachElements(sound, object)
        else
            local x,y,z = getElementPosition(player)
            local sound = playSound3D("assets/"..som..".wav", x, y, z)
            setSoundMinDistance( sound, 4)
            setSoundMaxDistance( sound, distance)
            setSoundVolume( sound, volume )
            attachElements(sound, player)
            local dim = getElementDimension(player)
            local int = getElementInterior(player)
            setElementDimension(sound, dim)
            setElementInterior(sound, int)
        end
    end
end
addEvent("playSounds", true)
addEventHandler("playSounds", root, PlaySounds)


